mapper.insert:
insert into gis_area (name, code, typename, classcode, geo, remark) values (
 #{name}, #{code}, #{typename}, #{classcode},
    ST_GeomFromText(#{geo},4326,'axis-order=long-lat'), #{remark}
    )

mapper.update:
UPDATE gis_area
SET geo = ST_GeomFromText(#{geo},4326,'axis-order=long-lat')
WHERE id = #{id}
